package first;

public class ex_10operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 1 + 2;
		System.out.println("1 + 2 = " + result);
		
		result = result - 1;
		
		result = result * 10;
		
		result = result / 5;
		
		System.out.println("Result =" + result);
		
		result = result % 2;
		System.out.println(result);
		
		result++;
		++result;
		
		System.out.println(result);
		
		result += 2;
		
		System.out.println(result);
		
		result *= 3;
		
		System.out.println(result);
	}

}
